Item.method_code:
var xpath = "Relationships/Item/related_id/Item[@id='" + relatedID + "']";
var relitm = item.selectSingleNode(xpath);
if (relitm === null) {
   return;
}

var state = top.aras.getItemProperty(relitm, "state");

if ( state == "Closed")
{
	alert("Not possible to delete closed Action Item");
	return false;
}

return;
